import React, { useState } from 'react';
import { Link } from 'react-scroll';
import Modal from '../Modal/Modal';
import './Menu.scss';

const Menu = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = () => {
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="header_nav">
      <div className="title">
        <h1>dhifallah chaala.</h1>
        <p>Web Developer</p>
      </div>
      <div className="flexLink">
        <Link
          to="flexlink"
          className="navbar"
          spy={true}
          smooth={true}
          offset={0}
          duration={500}
        >
          <p>ACCUEIL</p>
          <div className="slider"></div>
        </Link>
        <Link
          to="about"
          className="navbar"
          spy={true}
          smooth={true}
          offset={0}
          duration={500}
        >
          <p>ABOUT</p>
          <div className="slider"></div>
        </Link>
        <Link
          to="slider"
          className="navbar"
          spy={true}
          smooth={true}
          offset={50}
          duration={500}
        >
          <p>PORTFOLIO</p>
          <div className="slider"></div>
        </Link>
        <Link
          to="contact-form"
          className="navbar"
          spy={true}
          smooth={true}
          offset={50}
          duration={500}
        >
          <p>CONTACT</p>
          <div className="slider"></div>
        </Link>
      </div>
      <div className="burger-menu" onClick={openModal}>
        <span
          className={`burger-icon ${isModalOpen ? 'clicked' : 'unclicked'}`}
        >
          <i className="fa-solid fa-bars"></i>
        </span>
      </div>

      <Modal opened={isModalOpen} closeModal={closeModal}>
        <div className="modal-content">
          <Link
            to="flexlink"
            className="modal-link"
            spy={true}
            smooth={true}
            offset={0}
            duration={500}
          >
            ACCUEIL
          </Link>
          <Link
            to="about"
            className="modal-link"
            spy={true}
            smooth={true}
            offset={0}
            duration={500}
          >
            ABOUT
          </Link>
          <Link
            to="slider"
            className="modal-link"
            spy={true}
            smooth={true}
            offset={50}
            duration={500}
          >
            PORTFOLIO
          </Link>
          <Link
            to="contact-form"
            className="modal-link"
            spy={true}
            smooth={true}
            offset={50}
            duration={500}
          >
            CONTACT
          </Link>
        </div>
      </Modal>
    </div>
  );
};

export default Menu;
