import React, { useState } from 'react';
import { Link } from 'react-scroll';
import Modal from '../Modal/Modal';
import './Menu.scss';

const Menu = () => {
  const [isMenuClicked, setIsMenuClicked] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const openModal = () => {
    setIsModalOpen(true);
  };
  const updateMenu = () => {
    setIsMenuClicked(!isMenuClicked);
  };

  return (
    <div className="header_nav">
      <div className="title">
        <h1>dhifallah chaala.</h1>
        <p>Web Developer</p>
      </div>
      <div
        id="flexlink"
        className={`flexLink ${isMenuClicked ? 'responsive_nav' : ''}`}
      >
        <Link
          to="flexlink"
          className="navbar"
          spy={true}
          smooth={true}
          offset={0}
          duration={500}
        >
          <p>ACCUEIL</p>
          <div className="slider"></div>
        </Link>
        <Link
          to="about"
          className="navbar"
          spy={true}
          smooth={true}
          offset={0}
          duration={500}
        >
          <p>ABOUT</p>
          <div className="slider"></div>
        </Link>
        <Link
          to="slider"
          className="navbar"
          spy={true}
          smooth={true}
          offset={50}
          duration={500}
        >
          <p>PORTFOLIO</p>
          <div className="slider"></div>
        </Link>
        <Link
          to="contact-form"
          className="navbar"
          spy={true}
          smooth={true}
          offset={50}
          duration={500}
        >
          <p>CONTACT</p>
          <div className="slider"></div>
        </Link>
      </div>
      <Modal opened={isModalOpen} Content={<YourLinksComponent />}>
        {({ isOpened, setIsOpened }) => (
          <div className="burger-menu" onClick={openModal}>
            {isOpened ? (
              <span
                className={`burger-icon ${isOpened ? 'clicked' : 'unclicked'}`}
              >
                <i className="fa-solid fa-xmark"></i>
              </span>
            ) : (
              <span
                className={`burger-icon ${isOpened ? 'clicked' : 'unclicked'}`}
              >
                <i className="fa-solid fa-bars"></i>
              </span>
            )}
          </div>
        )}
      </Modal>
    </div>
  );
};

export default Menu;
