import React, { useState } from 'react';
import { Link } from 'react-scroll';
import './Menu.scss';
import Modal from '../Modal/Modal';

const Menu = () => {
  const [isMenuClicked, setIsMenuClicked] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const menuContent = (
    <div
      id="flexlink"
      className={`flexLink ${isMenuClicked ? 'responsive_nav' : ''}`}
    >
      <Link
        to="flexlink"
        className="navbar"
        spy={true}
        smooth={true}
        offset={0}
        duration={500}
      >
        <p>ACCUEIL</p>
        <div className="slider"></div>
      </Link>
      <Link
        to="about"
        className="navbar"
        spy={true}
        smooth={true}
        offset={0}
        duration={500}
      >
        <p>ABOUT</p>
        <div className="slider"></div>
      </Link>
      <Link
        to="slider"
        className="navbar"
        spy={true}
        smooth={true}
        offset={50}
        duration={500}
      >
        <p>PORTFOLIO</p>
        <div className="slider"></div>
      </Link>
      <Link
        to="contact-form"
        className="navbar"
        spy={true}
        smooth={true}
        offset={50}
        duration={500}
      >
        <p>CONTACT</p>
        <div className="slider"></div>
      </Link>
    </div>
  );

  const updateMenu = () => {
    setIsMenuClicked(!isMenuClicked);
  };

  return (
    <div className="header_nav">
      <div className="title">
        <h1>dhifallah chaala.</h1>
        <p>Web Developer</p>
      </div>
      <div className="hide">{isModalOpen ? null : menuContent}</div>
      <div className="burger-menu" onClick={() => setIsModalOpen(true)}>
        <div
          className={`burger-bar ${isMenuClicked ? 'clicked' : 'unclicked'}`}
        ></div>
        <div
          className={`burger-bar ${isMenuClicked ? 'clicked' : 'unclicked'}`}
        ></div>
        <div
          className={`burger-bar ${isMenuClicked ? 'clicked' : 'unclicked'}`}
        ></div>
      </div>
      {isModalOpen && (
        <Modal opened={isModalOpen} Content={menuContent}>
          {({ setIsOpened }) => (
            <>
              <div className="content">
                {menuContent}
                {/* Le bouton de fermeture du modal */}
                <button
                  className="close"
                  type="button"
                  onClick={() => setIsOpened(false)}
                >
                  <i className="fa-solid fa-xmark"></i>
                </button>
              </div>
            </>
          )}
        </Modal>
      )}
    </div>
  );
};

export default Menu;
