import React, { useState } from 'react';
import { Link } from 'react-scroll';
import Modal from '../Modal/Modal';
import './Menu.scss';

const Menu = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const menuContent = (
    <div className="flexLink">
      <Link
        to="flexlink"
        className="navbar"
        spy={true}
        smooth={true}
        offset={0}
        duration={500}
      >
        <p>ACCUEIL</p>
        <div className="slider"></div>
      </Link>
      <Link
        to="about"
        className="navbar"
        spy={true}
        smooth={true}
        offset={0}
        duration={500}
      >
        <p>ABOUT</p>
        <div className="slider"></div>
      </Link>
      <Link
        to="slider"
        className="navbar"
        spy={true}
        smooth={true}
        offset={50}
        duration={500}
      >
        <p>PORTFOLIO</p>
        <div className="slider"></div>
      </Link>
      <Link
        to="contact-form"
        className="navbar"
        spy={true}
        smooth={true}
        offset={50}
        duration={500}
      >
        <p>CONTACT</p>
        <div className="slider"></div>
      </Link>
    </div>
  );

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  return (
    <div className="header_nav">
      <div className="title">
        <h1>dhifallah chaala.</h1>
        <p>Web Developer</p>
      </div>
      <div className="hide">{isModalOpen ? null : menuContent}</div>
      <div className="burger-menu" onClick={handleOpenModal}>
        <span
          className={`burger-icon ${isModalOpen ? 'clicked' : 'unclicked'}`}
        >
          <i className="fa-solid fa-bars"></i>
        </span>
      </div>
      {isModalOpen && (
        <Modal opened={isModalOpen} Content={menuContent}>
          {({ setIsOpened }) => (
            <button
              className="close"
              type="button"
              data-testid="close-modal"
              onClick={() => {
                setIsOpened(false);
                setIsModalOpen(false);
              }}
            ></button>
          )}
        </Modal>
      )}
    </div>
  );
};

export default Menu;
