import React, { useState } from 'react';
import { Link } from 'react-scroll';
import './Menu.scss';
import Modal from '../Modal/Modal'

const Menu = () => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isMenuClicked, setIsMenuClicked] = useState(false);
 const updateMenu = () => {
    setIsMenuClicked(!isMenuClicked);
  };
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };
  const renderModalContent = (
    <div className="modal-content">
      <Link to="flexlink" className="navbar" onClick={toggleModal}>
        <p>ACCUEIL</p>
      </Link>
      <Link to="about" className="navbar" onClick={toggleModal}>
        <p>ABOUT</p>
      </Link>
      <Link to="slider" className="navbar" onClick={toggleModal}>
        <p>PORTFOLIO</p>
      </Link>
      <Link to="contact-form" className="navbar" onClick={toggleModal}>
        <p>CONTACT</p>
      </Link>
    </div>
  );
  return (
    <div className="header_nav">
      <div className="title">
        <h1>dhifallah chaala.</h1>
        <p>Web Developer</p>
      </div>
      <div
        id="flexlink"
        className={`flexLink ${isMenuClicked ? 'responsive_nav' : ''}`}
      >
       <renderModalContent />
      </div>
      <div className="burger-menu" onClick={toggleModal}>
        <div
          className={`burger-bar ${isMenuClicked ? 'clicked' : 'unclicked'}`}
        ></div>
        <div
          className={`burger-bar ${isMenuClicked ? 'clicked' : 'unclicked'}`}
        ></div>
        <div
          className={`burger-bar ${isMenuClicked ? 'clicked' : 'unclicked'}`}
        ></div>
      </div>
      <Modal opened={isModalOpen} Content={renderModalContent} setIsOpened={setIsModalOpen} />
    </div>
  );
};

export default Menu;
